import React, { useRef, useState } from 'react';
import emailjs from '@emailjs/browser';

const Contact = () => {
  const form = useRef();
  const [status, setStatus] = useState(null);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        'service_xrdjpo8',     // EmailJS ÏÑúÎπÑÏä§ ID
        'service_xrdjpo8',    // EmailJS ÌÖúÌîåÎ¶ø ID
        form.current,
        'YOUR_PUBLIC_KEY'      // EmailJS Í≥µÍ∞ú ÌÇ§ (API ÌÇ§)
      )
      .then(
        () => {
          setStatus('ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Î©îÏùºÏù¥ Ï†ÑÏÜ°ÎêòÏóàÏäµÎãàÎã§!');
          form.current.reset();
        },
        (error) => {
          setStatus('Î©îÏùº Ï†ÑÏÜ°Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
          console.error(error);
        }
      );
  };

  return (
    <section className="max-w-3xl mx-auto my-10 p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-4">üì¨ Contact</h2>
      <form ref={form} onSubmit={sendEmail} className="space-y-4">
        <div>
          <label className="block text-sm font-medium">Ïù¥Î¶Ñ</label>
          <input type="text" name="name" required className="w-full p-2 border rounded-md" />
        </div>
        <div>
          <label className="block text-sm font-medium">Ïù¥Î©îÏùº</label>
          <input type="email" name="email" required className="w-full p-2 border rounded-md" />
        </div>
        <div>
          <label className="block text-sm font-medium">Î©îÏãúÏßÄ</label>
          <textarea name="message" required rows="5" className="w-full p-2 border rounded-md" />
        </div>
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Î≥¥ÎÇ¥Í∏∞
        </button>
        {status && <p className="text-sm mt-2">{status}</p>}
      </form>
    </section>
  );
};

export default Contact;
