// src/components/About.jsx

import profileImage from '../profile.jpg'; // 이미지 경로에 맞게 수정
// 또는 public에 저장했다면: <img src="/profile.jpg" />

const About = () => {
  return (
    <section className="max-w-4xl mx-auto my-10 p-6 bg-white shadow-md rounded-xl">
      <h2 className="text-2xl font-bold mb-6 text-center">👋 About Me</h2>

      <div className="flex flex-col md:flex-row items-center md:items-start gap-6">
        {/* 프로필 이미지 */}
        <img
          src={profileImage}
          alt="프로필 사진"
          className="w-40 h-40 object-cover rounded-full border border-gray-300 shadow"
        />

        {/* 자기소개 텍스트 */}
        <div className="text-justify">
          <p className="mb-3">
            안녕하세요. 경기대학교 컴퓨터공학부 4학년에 재학 중인 <strong>조지관</strong>입니다.
          </p>
          <p className="mb-3">
            Java와 Spring Boot로 RESTful 백엔드를, React와 TypeScript로 SPA를 구현하며 풀스택 역량을 키웠고,
            MongoDB Change Streams + SSE 기반 실시간 알림, Swagger 기반 API 문서 자동화,
            Postman E2E 테스트(90% 커버리지)를 경험했습니다.
          </p>
          <p className="mb-3">
            캡스톤 프로젝트에서는 객체 탐지 AI를 구현해 <strong>논문 경진대회 은상</strong>,
            RAG 기반 차량 비서 시스템 백엔드로 <strong>교내 경진대회 은상</strong>을 수상했습니다.
          </p>
          <p className="mb-3">
            구조적 설계와 확장성, 안정성을 고려한 개발자로 성장하고 있습니다.
          </p>
        </div>
      </div>

      {/* 핵심 역량 */}
      <h3 className="text-xl font-semibold mt-8 mb-3">💡 핵심 역량</h3>
      <ul className="list-disc list-inside space-y-1 text-sm md:text-base">
        <li>Spring Boot 기반 RESTful API 서버 설계 및 AWS 클라우드 배포</li>
        <li>React + TypeScript 기반 SPA 구현 및 풀스택 개발 역량</li>
        <li>YOLO + TACO 데이터셋 기반 객체 탐지 AI 모델 구현 및 논문상 수상</li>
        <li>RAG 기반 차량 비서 백엔드 개발 및 교내 캡스톤 은상 수상</li>
        <li>Swagger 문서화 + Postman 기반 E2E 테스트 자동화 경험</li>
      </ul>

      {/* 기술 스택 */}
      <h3 className="text-xl font-semibold mt-8 mb-3">🛠 나의 스킬</h3>
      <div className="flex flex-wrap gap-2 text-sm">
        {[
          'Java', 'Spring Boot', 'REST API', 'AWS', 'TypeScript', 'ReactJS', 'Node.js',
          'MySQL', 'MongoDB', 'Python', 'YOLO', 'AI/인공지능', 'C', 'JavaScript',
          'Git', 'JSP', 'Postman', 'OAuth2'
        ].map(skill => (
          <span
            key={skill}
            className="bg-blue-100 text-blue-700 px-2 py-1 rounded-md"
          >
            {skill}
          </span>
        ))}
      </div>
    </section>
  );
};

export default About;
