import { motion } from 'framer-motion';

const About = () => {
  return (
    <motion.section
      className="max-w-3xl mx-auto my-10 p-6 bg-white shadow-md rounded-xl text-justify"
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
    >
      <h2 className="text-2xl font-bold mb-4">👋 About Me</h2>

      <p className="mb-4">
        안녕하세요. 경기대학교 컴퓨터공학부에 재학 중인 <strong>조지관</strong>입니다.
        풀스택 개발 경험과 AI 프로젝트 참여를 통해 <strong>문제 해결 중심의 백엔드 개발자</strong>로 성장하고 있습니다.
      </p>

      <p className="mb-4">
        Java/Spring 기반의 REST API 구축부터 MongoDB + SSE를 활용한 실시간 알림 시스템, YOLO 기반 객체 탐지 AI,
        Postman E2E 테스트까지 다양한 기술 스택을 경험하며
        <strong>안정성과 확장성을 고려한 서버 개발</strong>에 자신 있습니다.
      </p>

      <h3 className="text-xl font-semibold mt-6 mb-2">💡 핵심 역량</h3>
      <ul className="list-disc list-inside space-y-1">
        <li>Spring Boot 기반 API 서버 개발 및 AWS 배포 경험</li>
        <li>React + TypeScript 기반 SPA 구현 및 풀스택 협업</li>
        <li>YOLO + TACO Dataset 활용한 객체 탐지 AI 개발</li>
        <li>Postman 기반 E2E 테스트 자동화 (90% 이상 커버리지)</li>
        <li>MongoDB Change Streams + SSE 실시간 알림 시스템 구축</li>
      </ul>

      <h3 className="text-xl font-semibold mt-6 mb-2">🛠 기술 스택</h3>
      <div className="flex flex-wrap gap-2">
        {[
          'Java', 'Spring Boot', 'REST API', 'AWS', 'TypeScript', 'ReactJS', 'Node.js',
          'MySQL', 'MongoDB', 'Python', 'YOLO', 'AI/인공지능', 'JavaScript',
          'Git', 'Postman', 'OAuth2'
        ].map(skill => (
          <span
            key={skill}
            className="bg-blue-100 text-blue-700 px-2 py-1 rounded-md text-sm"
          >
            {skill}
          </span>
        ))}
      </div>
    </motion.section>
  );
};

export default About;
