import React, { useState } from "react";
import { motion } from "framer-motion";

const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Î©îÏùº Ï†ÑÏÜ° Í∏∞Îä•ÏùÄ ÏïÑÏßÅ Íµ¨ÌòÑÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§.");
    // Ïó¨Í∏∞ÏÑú EmailJS ÎòêÎäî Î∞±ÏóîÎìú Ïó∞Í≤∞ Î°úÏßÅÏùÑ ÏûëÏÑ±Ìï† Ïàò ÏûàÏäµÎãàÎã§.
  };

  return (
    <motion.section
      className="max-w-4xl mx-auto my-10 p-6 bg-white shadow-md rounded-xl"
      initial={{ opacity: 0, y: 30 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.6, delay: 0.1 }}
    >
      <h2 className="text-2xl font-bold mb-4">üì© Contact</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Ïù¥Î¶Ñ"
          value={formData.name}
          onChange={handleChange}
          className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Ïù¥Î©îÏùº"
          value={formData.email}
          onChange={handleChange}
          className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring"
          required
        />
        <textarea
          name="message"
          placeholder="Î¨∏Ïùò ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
          value={formData.message}
          onChange={handleChange}
          className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring h-32 resize-none"
          required
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Î≥¥ÎÇ¥Í∏∞
        </button>
      </form>
    </motion.section>
  );
};

export default Contact;
