import React, { useState } from 'react';
import About from './components/About';
import Info from './components/Info';
import { motion, AnimatePresence } from "framer-motion";

function App() {
  const [showOthers, setShowOthers] = useState(false);

  return (
    <div className="min-h-screen bg-gray-100 text-gray-800 p-6 space-y-10">
      {/* ÏÜåÍ∞ú Î∞è ÌïµÏã¨ Ïó≠Îüâ */}
      <About />

      {/* ÌïôÎ†•, ÏûêÍ≤©Ï¶ù, ÏàòÏÉÅ Îì± */}
      <Info />

      {/* Ï£ºÏöî ÌîÑÎ°úÏ†ùÌä∏ */}
      <motion.section
  id="projects"
  className="max-w-4xl mx-auto scroll-mt-20"
  initial={{ opacity: 0, y: 30 }}
  whileInView={{ opacity: 1, y: 0 }}
  viewport={{ once: true }}
  transition={{ duration: 0.6, delay: 0.2 }}
>
        <h2 className="text-2xl font-bold mb-4">üíº Ï£ºÏöî ÌîÑÎ°úÏ†ùÌä∏</h2>
        <div className="space-y-6">
          <Project
            title="movie_diary"
            description="ÏÇ¨Ïö©Ïûê ÏùºÍ∏∞Î•º Î∂ÑÏÑùÌï¥ Í∞êÏ†ïÏùÑ Î∂ÑÎ•òÌïòÍ≥†, Í∑∏Ïóê ÎßûÎäî ÏòÅÌôîÎ•º Ï∂îÏ≤úÌïòÎäî AI Í∞êÏÑ± Í∏∞Î∞ò ÏÑúÎπÑÏä§ÏûÖÎãàÎã§. ReactÏôÄ Flask Í∏∞Î∞òÏúºÎ°ú Íµ¨ÌòÑÌñàÏäµÎãàÎã§."
            link="https://github.com/crushonyou2/movie_diary"
          />
          <Project
            title="gildongE"
            description="Spring BootÏôÄ MongoDB Í∏∞Î∞ò Ï∞®Îüâ Ïñ¥ÏãúÏä§ÌÑ¥Ìä∏ Î∞±ÏóîÎìú ÏãúÏä§ÌÖú. Ïã§ÏãúÍ∞Ñ ÏïåÎ¶º, ÏÜåÎ™®Ìíà ÍµêÏ≤¥ Ï£ºÍ∏∞ Í≥ÑÏÇ∞, API ÏÑ§Í≥Ñ Î∞è Î∞∞Ìè¨Î•º Îã¥ÎãπÌñàÏäµÎãàÎã§."
            link="https://github.com/crushonyou2/gildongE"
            poster="/gildongE_poster.jpg"
          />
          <Project
            title="Build-Your-Health"
            description="React Í∏∞Î∞ò Í±¥Í∞ï Î£®Ìã¥ Í¥ÄÎ¶¨ Ïï±. ÏÇ¨Ïö©Ïûê ÏÉÅÌÉú Îç∞Ïù¥ÌÑ∞Î•º Í∏∞Î°ùÌïòÍ≥† Î™©Ìëú Í∏∞Î∞ò Î£®Ìã¥ Í¥ÄÎ¶¨ Í∏∞Îä•ÏùÑ Íµ¨ÌòÑÌñàÏäµÎãàÎã§."
            link="https://github.com/crushonyou2/Build-Your-Health"
          />
          <Project
            title="kakao-clone"
            description={
              <>
                ÎÖ∏ÎßàÎìúÏΩîÎçî Ï±åÎ¶∞ÏßÄ Í∏∞Î∞òÏùò Ïπ¥Ïπ¥Ïò§ÌÜ° ÌÅ¥Î°† UI ÌîÑÎ°úÏ†ùÌä∏ÏûÖÎãàÎã§. Ìï¥Îãπ Í∏∞Ïàò Ïö∞ÏàòÏûëÏúºÎ°ú ÏÑ†Ï†ïÎêòÏóàÏúºÎ©∞,
                HTML/CSS/JS Í∏∞Î∞òÏúºÎ°ú Î∞òÏùëÌòï UIÏôÄ Î©îÏãúÏßÄ Î¶¨Ïä§Ìä∏ Ïù∏ÌÑ∞ÌéòÏù¥Ïä§Î•º Íµ¨ÌòÑÌñàÏäµÎãàÎã§.
                <br />
                <a
                  href="https://nomadcoders.co/community/thread/8730"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-sm text-blue-500 underline block mt-2"
                >
                  Ïö∞ÏàòÏûë ÏÑ†Ï†ï Í≤åÏãúÍ∏Ä Î≥¥Í∏∞ ‚Üí
                </a>
              </>
            }
            link="https://github.com/crushonyou2/kakao-clone"
          />
        </div>
      </motion.section>

      {/* Í∏∞ÌÉÄ ÌîÑÎ°úÏ†ùÌä∏ (ÌÜ†Í∏Ä) */}
      <motion.section
  className="max-w-3xl mx-auto"
  initial={{ opacity: 0, y: 30 }}
  whileInView={{ opacity: 1, y: 0 }}
  viewport={{ once: true }}
  transition={{ duration: 0.6, delay: 0.3 }}
>
        <div className="mt-10">
          <button
            className="text-blue-600 hover:underline text-sm"
            onClick={() => setShowOthers(!showOthers)}
          >
            {showOthers ? 'Í∏∞ÌÉÄ ÌîÑÎ°úÏ†ùÌä∏ Îã´Í∏∞ ‚ñ≤' : 'Í∏∞ÌÉÄ ÌîÑÎ°úÏ†ùÌä∏ Î≥¥Í∏∞ ‚ñº'}
          </button>
        </div>

        <AnimatePresence>
          {showOthers && (
            <motion.div
              key="others"
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.3 }}
              className="mt-4 space-y-4 text-gray-700 text-sm"
            >
              <MiniProject
                title="dragonball"
                description="PC Î∂ÄÌíà Ï†ïÎ≥¥Î•º ÎπÑÍµê Î∂ÑÏÑùÌïòÎäî Í∞ÄÍ≤© ÎπÑÍµê Ïõπ ÏÑúÎπÑÏä§"
                link="https://github.com/crushonyou2/dragonball"
              />
              <MiniProject
                title="dynamic_scraper"
                description="BeautifulSoup + Playwright Í∏∞Î∞ò Ïõπ ÌÅ¨Î°§ÎßÅ Ïó∞Ïäµ"
                link="https://github.com/crushonyou2/dynamic_scraper"
              />
              <MiniProject
                title="momentum"
                description="ÎÇ†Ïî®¬∑Ïù∏ÏÇøÎßê¬∑Ìà¨ÎëêÎ¶¨Ïä§Ìä∏Î•º Íµ¨ÌòÑÌïú Î™®Î©òÌÖÄ Ïï± ÌÅ¥Î°†"
                link="https://github.com/crushonyou2/momentum"
              />
            </motion.div>
          )}
        </AnimatePresence>
      </motion.section>

      {/* Ìë∏ÌÑ∞ */}
      <footer className="mt-16 text-sm text-gray-500 text-center">
        ¬© 2025 Ï°∞ÏßÄÍ¥Ä ‚Äî GitHub: <a href="https://github.com/crushonyou2" className="underline">crushonyou2</a>
      </footer>
    </div>
  );
}

function Project({ title, description, link, poster }) {
  const [showPoster, setShowPoster] = useState(false);

  return (
    <div className="bg-white p-4 rounded-xl shadow-md">
      <h3 className="text-xl font-semibold">{title}</h3>
      <p className="mt-1">{description}</p>
      <a
        href={link}
        target="_blank"
        rel="noopener noreferrer"
        className="inline-block mt-2 text-blue-600 hover:underline"
      >
        GitHub Î≥¥Í∏∞ ‚Üí
      </a>

      {/* Ìè¨Ïä§ÌÑ∞ ÌÜ†Í∏Ä */}
      {poster && (
        <div className="mt-3">
          <button
            onClick={() => setShowPoster(!showPoster)}
            className="text-sm text-blue-500 hover:underline"
          >
            {showPoster ? 'Ìè¨Ïä§ÌÑ∞ Îã´Í∏∞ ‚ñ≤' : 'ÌîÑÎ°úÏ†ùÌä∏ Ìè¨Ïä§ÌÑ∞ Î≥¥Í∏∞ ‚ñº'}
          </button>
          <AnimatePresence>
            {showPoster && (
              <motion.img
                key="poster"
                src={poster}
                alt={`${title} Ìè¨Ïä§ÌÑ∞`}
                className="mt-2 rounded-md shadow max-h-[500px] w-auto"
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.95 }}
                transition={{ duration: 0.3 }}
              />
            )}
          </AnimatePresence>
        </div>
      )}
    </div>
  );
}


function MiniProject({ title, description, link }) {
  return (
    <div className="border-l-4 border-blue-300 pl-3">
      <a href={link} target="_blank" rel="noopener noreferrer" className="text-blue-600 font-semibold hover:underline">
        {title}
      </a>
      <p>{description}</p>
    </div>
  );
}

export default App;
