import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';

const Contact = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm(
      'service_xrdjpo8',     // ‚Üê Ïó¨Í∏∞Ïóê Î≥µÏÇ¨Ìïú ÏÑúÎπÑÏä§ ID
      'template_hndz1ts',    // ‚Üê Ïó¨Í∏∞Ïóê Î≥µÏÇ¨Ìïú ÌÖúÌîåÎ¶ø ID
      form.current,
      'GoLqvXvUesYtb-S9G'      // ‚Üê Ïó¨Í∏∞Ïóê Î≥µÏÇ¨Ìïú ÌçºÎ∏îÎ¶≠ ÌÇ§
    )
    .then(
      () => {
        alert('Î¨∏ÏùòÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†ÑÏÜ°ÎêòÏóàÏäµÎãàÎã§.');
        form.current.reset();
      },
      (error) => {
        alert('Ï†ÑÏÜ° Ï§ë Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ' + error.text);
      }
    );
  };

  return (
    <section className="max-w-3xl mx-auto p-6 bg-white shadow-md rounded-xl mt-10">
      <h2 className="text-2xl font-bold mb-4">üìÆ Contact</h2>
      <form ref={form} onSubmit={sendEmail} className="space-y-4">
        <input type="text" name="name" placeholder="Ïù¥Î¶Ñ" required className="w-full p-2 border rounded" />
        <input type="email" name="email" placeholder="Ïù¥Î©îÏùº" required className="w-full p-2 border rounded" />
        <textarea name="message" placeholder="ÎÇ¥Ïö©" required rows="5" className="w-full p-2 border rounded" />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
          Î≥¥ÎÇ¥Í∏∞
        </button>
      </form>
    </section>
  );
};

export default Contact;
